import {handleActions} from '@letapp/redux-actions';
import * as actions from './appActions';

const INITIAL_STATE = {
    fullLeftMenu: false,
    showPlayPanel: true,
    isLoading: true,
    isError: false,
    error: null
};


export default handleActions({
    [actions.initialization.start] : state => ({
        ...state,
        isLoading: true,
        isError: false,
        error: null
    }),
    [actions.initialization.success] : state => ({
        ...state,
        isLoading: false,
    }),
    [actions.initialization.error] : (state, action) => ({
        ...state,
        isLoading: false,
        isError: true,
        error: action.payload.error
    }),


    [actions.toggleLeftMenu] : (state) => ({
        ...state,
        fullLeftMenu: !state.fullLeftMenu
    }),
    [actions.togglePlayPanel] : (state) => ({
        ...state,
        showPlayPanel: !state.showPlayPanel
    }),

}, INITIAL_STATE);
