import React from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';
import RecentlyPlayed from "../../components/RecentlyPlayed/RecentlyPlayed";

var request = require('request'); // "Request" library
var client_id = '598296e6ebbb452199ca93a5c17b167e'; // Your client id
var client_secret = '2111b152e7e74348983a50799a512d98'; // Your secret`


class Home extends React.Component {


    componentDidMount() {

        // your application requests authorization
        var authOptions = {
            url: 'https://accounts.spotify.com/api/token',
            headers: {
                'Authorization': 'Basic ' + (new Buffer(client_id + ':' + client_secret).toString('base64'))
            },
            form: {
                grant_type: 'client_credentials'
            },
            json: true
        };



        request.post(authOptions, function(error, response, body) {

            console.log(error);
            if (!error && response.statusCode === 200) {

                // use the access token to access the Spotify Web API
                var token = body.access_token;

                console.log({token});


                var options = {
                    url: 'https://api.spotify.com/v1/browse/categories/party/playlists?country=BR&limit=2',
                    headers: {
                        'Authorization': 'Bearer ' + token
                    },
                    json: true
                };
                request.get(options, function(error, response, body) {
                    console.log(body);
                });
            }
        });



    }


    render() {

        return (
            <RecentlyPlayed />
        )
    }
}


Home.propTypes = {

};

export default Home;
