import React, {useRef, useState} from 'react';
import PropTypes from 'prop-types';

// import Vizyalization from "../AudioVizyalizations/Vizyalization";

import PlayPanelRight from './PlayPanelRight'
import PlayPanelProgressBar from './PlayPanelProgressBar'
import PlayPanelAudioInfo from './PlayPanelAudioInfo'
import {normalizeAudioCurentTime} from '../../helpers/helpers';
import './playerPanel.scss';

import {
    IoIosPlay,
    IoIosPause,
    IoMdSkipForward,
    IoMdSkipBackward
} from "react-icons/io";

const PlayPanel = ({
                       audioList,
                       currentAudio,
                       loop,
                       audio,
                       handleMetadata,
                       handleTimeupdate,
                       isPlaying,
                       handlePlay,
                       handlePlayNext,
                       volume,
                       defaultValue,
                       changeVolume,
                       handleAudioLoop,
                       setAudioDuration,
                       currentTrackMoment,
                       currentTrackDuration
                   }) => {

    return (
        <div className="ms_player_wrapper">
            <div className="ms_player_close">
                <i className="fa fa-angle-up" aria-hidden="true"/>
            </div>
            <div className="player_mid">
                <div className="audio-player">

                    <div className="jp-jplayer" style={{display: 'none'}}>

                        {/*<audio*/}
                        {/*loop={loop}*/}
                        {/*ref={audioPlayer}*/}
                        {/*preload="metadata"*/}
                        {/*onLoadedMetadata={handleMetadata}*/}
                        {/*onTimeUpdate={() => handleTimeupdate()}*/}
                        {/*>*/}
                        {/*<source*/}
                        {/*src={audioList[currentAudio].url}*/}
                        {/*type="audio/ogg"*/}
                        {/*/>*/}
                        {/*Ooops, your browser is sooo old.*/}
                        {/*</audio>*/}
                    </div>

                    <div className='jp-audio'>

                        <PlayPanelProgressBar
                            audio={audio}
                            setAudioDuration={setAudioDuration}
                        />

                        <PlayPanelAudioInfo
                            isPlaying={isPlaying}
                            currentTrackDuration={currentTrackDuration || '0:00'}
                            currentTrackMoment={normalizeAudioCurentTime(currentTrackMoment)}
                            audio={audio}
                        />

                        <div className="jp-controls flex-item">
                            <button className="jp-previous">
                                <IoMdSkipBackward size={25} color={'white'}/>
                            </button>

                            <button
                                className="jp-play"
                                onClick={handlePlay}
                            >
                                {isPlaying
                                    ? <IoIosPause size={50} style={{transform: 'translateX(2px)'}} color={'white'}/>
                                    : <IoIosPlay size={50} style={{transform: 'translateX(5px)'}} color={'white'}/>
                                }

                            </button>

                            <button
                                className="jp-next"
                                onClick={handlePlayNext}
                            >
                                <IoMdSkipForward size={25} color={'white'}/>
                            </button>
                        </div>

                        <PlayPanelRight
                            volume={volume}
                            defaultValue={defaultValue}
                            changeVolume={changeVolume}

                            loop={loop}
                            handleAudioLoop={handleAudioLoop}
                        />

                    </div>

                </div>
            </div>

        </div>
    );
};

PlayPanel.propTypes = {
    audioList: PropTypes.array,
    currentAudio: PropTypes.number,
};

export default PlayPanel;
