import React, {Component} from 'react';
import PropTypes from 'prop-types';

import {connect} from 'react-redux';
import {playNextAudio} from '../../modules/audio/audioAction';



import PlayPanel from './PlayPanel';

const defaultVolume = 60;

class PlayPanelContainer extends Component {

    constructor(props) {
        super(props);

        this.state = {
            audio: new Audio(props.audioList[props.currentAudio].url),
            duration: 0,
            currentTime: 0,
            isPlaying: false,
            isLoading: true,
            volume: defaultVolume,
            loop: false
        };
    }


    handlePlay = () => {
        const {audio} = this.state;

        if (audio.paused || audio.ended) {
            audio.play();
            this.setState({ isPlaying: true });
        } else {
            audio.pause();
            this.setState({ isPlaying: false });
        }
    };


    // getMetadata = () => {
    //     const {audio} = this.state;
    //     const duration = Math.floor(audio.duration);
    //     console.log(duration);
    // };


    handlePlayNext = () => {

        this.setState({isLoading: true});
        const {audio} = this.state;

        this.props.playNextAudio();

        audio.src = this.props.audioList[this.props.currentAudio].url;

        audio.onloadedmetadata = () => {
            console.log(audio.duration);
            this.setState({
                isLoading: false,
                isPlaying: true
            })
        };
        audio.ontimeupdate = () => {
            this.setState({
                currentTime: audio.currentTime
            })
        };
        audio.play();



    };


    changeVolume = value => {
        const {audio} = this.state;

        this.setState({volume: value});
        audio.volume = value / 100;
    };

    handleAudioLoop = () => {
        const {audio} = this.state;
        audio.loop = true;
        this.setState({
            loop: !this.state.loop
        });
    };


    // componentWillReceiveProps(nextProps, nextContext) {
    //     this.setNewCurrentAudio(nextProps.currentAudio);
    // }


    componentDidMount() {
        const {audio} = this.state;
        audio.onloadedmetadata = () => {
            this.setState({
                isLoading: false,
                duration: audio.duration
            })
        };
        audio.ontimeupdate = () => {
            this.setState({
                currentTime: audio.currentTime
            })
        }
    }


    render() {
        const {audio, isPlaying, volume, isLoading, duration, currentTime, loop} = this.state;

        return (
            <PlayPanel
                isLoading={isLoading}
                currentAudioObj={this.props.audioList[this.props.currentAudio]}
                audio={audio}
                isPlaying={isPlaying}
                handlePlay={this.handlePlay}
                handlePlayNext={this.handlePlayNext}
                volume={volume}
                changeVolume={this.changeVolume}
                duration={duration}
                currentTime={currentTime}
                loop={loop}
                handleAudioLoop={this.handleAudioLoop}

                // defaultValue={defaultValue}
                // audioPlayer={audioPlayer}
                // handleMetadata={handleMetadata}
                // handleTimeupdate={handleTimeupdate}
                // setAudioDuration={setAudioDuration}
                // currentTrackMoment={currentTrackMoment}
                // currentTrackDuration={currentTrackDuration}
                // currentAudio={currentAudio}
            />
        );
    }
}

PlayPanelContainer.propTypes = {
    audioList: PropTypes.array,
    currentAudio: PropTypes.number,
    playNextAudio: PropTypes.func,
};

export default connect(state => ({
    audioList: state.audio.audioList,
    currentAudio: state.audio.currentAudio
}), {playNextAudio})(PlayPanelContainer);







