import React from 'react';
import PropTypes from 'prop-types';
import Slider  from 'rc-slider';
import styled from 'styled-components';

const createSliderWithTooltip = Slider.createSliderWithTooltip;
const SliderWithTooltip = createSliderWithTooltip(Slider);

const PlayPanelProgressBar = ({currentTrackDuration, currentTrackMoment, setCurrentTrackMoment}) => {


    console.log(currentTrackMoment);
    // const currentTrackDurationNumber = currentTrackDuration.replace(' : ','.');
    const currentTrackMomentNumber = currentTrackMoment.includes(' : ') ?  currentTrackMoment.replace(' : ','.') : currentTrackMoment;


    if (!currentTrackDuration) return <p>loading</p>

    return (
        <ProgresWrap>
            <SliderWithTooltip
                value={+currentTrackMomentNumber}
                min={0}
                max={ +currentTrackDurationNumber }
                step={0.01}
                onChange={ value => setCurrentTrackMoment(value) }
            />
        </ProgresWrap>
    );
};

PlayPanelProgressBar.propTypes = {

};


const ProgresWrap = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  .rc-slider {
    padding: 0;
  }
`;

export default PlayPanelProgressBar;
