import React from 'react';
import PropTypes from 'prop-types';
import "react-id-swiper/src/styles/scss/swiper.scss";
import Swiper from 'react-id-swiper';

import image from '../../assets/images/music/r_music1.jpg';
import play from '../../assets/images/svg/play.svg';
import more from '../../assets/images/svg/more.svg';


import './recentlyPlayer.scss'

const RecentlyPlayed = props => {
    const params = {
        slidesPerView: 6,
        spaceBetween: 30,
        loop: false,
        speed: 1500,
        navigation: {
            nextEl: '.swiper-button-next5',
            prevEl: '.swiper-button-prev5',
        },
        breakpoints: {
            1800: {
                slidesPerView: 4,
            },
            1400: {
                slidesPerView: 4,
            },
            992: {
                slidesPerView: 2,
                spaceBetween: 10,
            },
            768: {
                slidesPerView: 2,
                spaceBetween: 10,
            },
            640: {
                slidesPerView: 1,
                spaceBetween: 15,
            },
            480: {
                slidesPerView: 1,
            },
            375: {
                slidesPerView: 1,
                spaceBetween: 0,
            }
        },
    }

    return (

        <div className='ms_rcnt_slider'>
            <div className="ms_heading">
                <h1>Recently Played</h1>
                <span className="veiw_all"><a href="#">view more</a></span>
            </div>

            <Swiper {...params}>


                <div className="swiper-slide">
                    <div className="ms_rcnt_box">
                        <div className="ms_rcnt_box_img">
                            <img src={image} alt=""/>
                            <div className="ms_main_overlay">
                                <div className="ms_box_overlay"/>
                                <div className="ms_more_icon">
                                    <img src={more} alt=""/>
                                </div>
                                <ul className="more_option">
                                    <li><a href="#"><span className="opt_icon"><span
                                        className="icon icon_fav"/></span>Add To Favourites</a></li>
                                    <li><a href="#"><span className="opt_icon"><span
                                        className="icon icon_queue"/></span>Add To Queue</a></li>
                                    <li><a href="#"><span className="opt_icon"><span
                                        className="icon icon_dwn"/></span>Download Now</a></li>
                                    <li><a href="#"><span className="opt_icon"><span
                                        className="icon icon_playlst"/></span>Add To Playlist</a></li>
                                    <li><a href="#"><span className="opt_icon"><span
                                        className="icon icon_share"/></span>Share</a></li>
                                </ul>
                                <div className="ms_play_icon">
                                    <img src={play} alt=""/>
                                </div>
                            </div>
                        </div>
                        <div className="ms_rcnt_box_text">
                            <h3><a href="#">Dream Your Moments (Duet)</a></h3>
                            <p>Ava Cornish &amp; Brian Hill</p>
                        </div>
                    </div>
                </div>

            </Swiper>

        </div>


    )
};

RecentlyPlayed.propTypes = {};

export default RecentlyPlayed;
