import React, {useRef} from 'react';
import PropTypes from 'prop-types';

import Slider  from 'rc-slider';
import 'rc-slider/assets/index.css';
import volumeImage from '../../assets/images/svg/volume.svg';

const createSliderWithTooltip = Slider.createSliderWithTooltip;
const SliderWithTooltip = createSliderWithTooltip(Slider);





const PlayPanelVolume = ({changeVolume, defaultValue, volume}) => {


    console.log(volume);

    return (
        <div className="jp-volume-wrap">

            <div className="jp-volume-image">
                <img
                    src={volumeImage}
                    alt="volume"
                    onClick={volume === 0 ? changeVolume(0) : changeVolume(defaultValue)}
                />
            </div>

             <SliderWithTooltip
                 defaultValue={defaultValue}
                 onChange={value => changeVolume(value)}
                 tipFormatter={value => `${value}%`}
             />

        </div>
    );
};

PlayPanelVolume.propTypes = {
    changeVolume: PropTypes.func.isRequired,
    defaultValue: PropTypes.bool.isRequired,
};

export default PlayPanelVolume;
