import React, {useRef} from 'react';
import PropTypes from 'prop-types';
import Nouislider from "nouislider-react";
import "nouislider/distribute/nouislider.css";
import volume from '../../assets/images/svg/volume.svg';




const PlayPanelVolume = props => {

    let volumeRef = useRef();


    const handleMouseDown = () => {
        alert(1);
    };

    return (
        <div className="jp-volume-controls flex-item">
            <div className="widget knob-container">
                <div className="knob-wrapper-outer">
                    <div
                        className="knob-wrapper"
                        onMouseDown={handleMouseDown}
                    >
                        <div className="knob-mask">
                            <div
                                className="knob d3"
                                ref={volumeRef}
                            ><span /></div>
                            <div className="handle" />
                            <div className="round">
                                <img src={volume} alt="" />
                            </div>
                        </div>
                    </div>

                </div>
            </div>
        </div>
    );
};

PlayPanelVolume.propTypes = {

};

export default PlayPanelVolume;
