const SliderWithTooltip = createSliderWithTooltip(Slider);

const PlayPanelProgressBar = ({currentTrackDuration, currentTrackMoment, setProgressValue}) => {

    const currentTrackDurationNumber = currentTrackDuration.replace(' : ','.');
    const currentTrackMomentNumber = currentTrackMoment.toString().includes(':') ?  currentTrackMoment.replace(' : ','.') : currentTrackMoment;

    if (!currentTrackDuration) return <p>loading</p>

