import React, {useRef, useState} from 'react';
import PropTypes from 'prop-types';

import PlayPanelVolume from './PlayPanelVolume'
import PlayPanelProgressBar from './PlayPanelProgressBar'
import {normalizeAudioCurentTime} from '../../helpers/helpers';
import './playerPanel.scss';
import m from '../../assets/audio/_Лучшая Музыка 2019 🌴 Зарубежные песни Хиты 🌴 Популярные Песни Слушать Бесплатно 2019_.mp3';


import Vizyalization from "../AudioVizyalization/Vizyalization";
import {audioSetup} from "../AudioVizyalization/animation";


const m1 = 'https://res.cloudinary.com/g-r-a-w-d-a-n-i-n/video/upload/v1561551649/MEGA_HITS_2019_Summer_Special_DG_Mix_2019_Best_Of_Deep_House_Sessions_Music_Chill_Out_Mix.mp3';

const smoothingTimeConstant = 0.65;
const fftSize = 8192;//32, 64, 128, 256, 512, 1024, 2048, 4096, 8192, 16384, 32768



const PlayPanel = () => {

    let audioPlayer = useRef();
    const defaultValue = 60;

    const [isPlaying, setIsPlaying] = useState(false);
    const [volume, setVolume] = useState(defaultValue);
    const [progressBarWidth, setProgressBarWidth] = useState('0');
    const [currentTrackDuration, setCurrentTrackDuration] = useState(0);
    const [currentTrackMoment, setCurrentTrackMoment] = useState(0);



    const handlePlay = () => {

        const audioRef = audioPlayer.current;

        if (audioRef.paused || audioRef.ended) {
            audioRef.play();
            setIsPlaying(true);


            let audioContext = new (window.AudioContext || window.webkitAudioContext)();
            let analyser = audioContext.createAnalyser();

            analyser.connect(audioContext.destination);
            analyser.smoothingTimeConstant = smoothingTimeConstant;
            analyser.fftSize = fftSize;
            // let analyserBufferLength = analyser.frequencyBinCount;

            // let audioSrc = audioContext.createMediaElementSource(audioRef);
            // audioSrc.connect(analyser);

            audioSetup(audioRef, analyser);


        } else {
            audioRef.pause();
            setIsPlaying(false);
        }
    };


    const changeVolume = value => {
        setVolume(value);
        audioPlayer.current.volume = value / 100
    };

    const handleMetadata = () => {
        const audio = audioPlayer.current;

        const duration = Math.floor(audio.duration);
        setCurrentTrackDuration(normalizeAudioCurentTime(duration));
    };


    const setAudioDuration = diration => {
        console.log(diration);
        audioPlayer.current.currentTime = diration;
    };


    const handleTimeupdate = () => {
        setCurrentTrackMoment(Math.floor(audioPlayer.current.currentTime));
        setProgressBarWidth(
            (audioPlayer.current.currentTime / audioPlayer.current.duration) * 100 + '%'
        );
        if (audioPlayer.current.currentTime === audioPlayer.current.duration) {
            alert('end');
        }
    };

    return (

        <div className="ms_player_wrapper">
            <div className="ms_player_close">
                <i className="fa fa-angle-up" aria-hidden="true"/>
            </div>
            <div className="player_mid">
                <div className="audio-player">

                    <div className="jp-jplayer" style={{display: 'none'}}>

                        <audio
                            ref={audioPlayer}
                            preload="metadata"
                            onLoadedMetadata={handleMetadata}
                            onTimeUpdate={() => handleTimeupdate()}
                        >
                            <source
                                src={m1}
                                type="audio/ogg"
                            />
                            Ooops, your browser is sooo old.
                        </audio>
                    </div>

                    {audioPlayer.current && (
                        <div className={`${isPlaying ? 'jp-state-playing' : ''} jp-audio`}>

                            <Vizyalization />


                            <PlayPanelProgressBar
                                audio={audioPlayer.current}
                                setAudioDuration={setAudioDuration}
                            />

                            <div className="jp-type-playlist">
                                <div className="jp-gui jp-interface flex-wrap">
                                    <div className="jp-controls flex-item">
                                        <button className="jp-previous" tabIndex="0">
                                            <i className="ms_play_control"/>
                                        </button>

                                        <button
                                            className="jp-play"
                                            onClick={handlePlay}
                                        >
                                            <i className="ms_play_control"/>
                                        </button>

                                        <button className="jp-next" tabIndex="0">
                                            <i className="ms_play_control"/>
                                        </button>
                                    </div>
                                    <div className="jp-progress-container flex-item">
                                        <div className="jp-time-holder">
                                        <span className="jp-current-time">
                                            {normalizeAudioCurentTime(currentTrackMoment)}
                                        </span>
                                            <span className="jp-duration">
                                            {currentTrackDuration || '0 : 00'}
                                        </span>
                                        </div>


                                        <div className="jp-progress">
                                            <div className="jp-seek-bar">
                                                <div className="jp-play-bar" style={{width: progressBarWidth}}>
                                                    <div className="bullet">
                                                    </div>
                                                </div>
                                            </div>
                                        </div>


                                    </div>

                                    <div className="jp-toggles flex-item">
                                        <button className="jp-shuffle" tabIndex="0" title="Shuffle">
                                            <i className="ms_play_control"/></button>
                                        <button className="jp-repeat" tabIndex="0" title="Repeat"><i
                                            className="ms_play_control"/></button>
                                    </div>

                                    <PlayPanelVolume
                                        volume={volume}
                                        defaultValue={defaultValue}
                                        changeVolume={changeVolume}/>

                                </div>
                            </div>
                        </div>
                    )}

                </div>
            </div>

        </div>
);
};

PlayPanel.propTypes = {

};

export default PlayPanel;
